mapper.insert:
INSERT INTO "REVIEWS" 
	  		(rv_title, rv_content, rv_image, u_no, rv_regdate

	  		, file_name, origin_file_name, file_size, file_path
	  		)
VALUES	 	(#{rv_title}, #{rv_content}, #{rv_image}, #{u_no}, NOW()

			, #{file_name}, #{origin_file_name}, #{file_size}, #{file_path}
			)

mapper.update:
UPDATE "REVIEWS" SET
		rv_title 	= #{rv_title},
		rv_content  = #{rv_content}
WHERE	rv_no 		= #{rv_no}

UPDATE	"REVIEWS"
SET		rv_cnt = rv_cnt + 1
WHERE	rv_no = #{rv_no}

mapper.select:
	select  rv_title, rv_content, rv_no, to_char(rv_regdate,'YYYY-MM-DD HH24:MI') rv_regdate
	from (
		   select *,
		   row_number () over(order by rv_no desc) as rnum
	 	   from "REVIEWS"
	 	   where u_no = #{u_no}
 		 ) rv
	where rnum between #{rowStart} AND #{rowEnd}
	order by rv_no desc
