mapper.insert:
INSERT INTO "REVIEWS" 
	  		(rv_title, rv_content, rv_image, rv_tag, rv_regdate

	  		, file_name, origin_file_name, file_size, file_path
	  		)
VALUES	 	(#{rv_title}, #{rv_content}, #{rv_image}, #{rv_tag}
			,  NOW()

			, #{file_name}, #{origin_file_name}
			, #{file_size}, #{file_path}
			)

mapper.update:
UPDATE "REVIEWS" SET
		rv_title 	= #{rv_title},
		rv_content  = #{rv_content},
		rv_tag		= #{rv_tag}
WHERE	rv_no 		= #{rv_no}

mapper.select:
	select *
	from (
		   select *,
		   row_number () over(order by rv_no desc) as rnum
	 	   from "REVIEWS"
	 	   where u_no = 15
 		 ) rv
	where rnum between #{rowStart} AND #{rowEnd}
	order by rv_no desc
